import React from 'react';
import logo from './logo.svg';
import './App.css';
import {connect} from 'react-redux'

function App(props) {
  return (
    <div className="App">
    <h1>"Readx Demo"</h1>
  <h1>"Getting data from centralStore {props.myName}"</h1>
  <button>Change It</button>
    </div>
  );
}

const mapTostateProps = (state) =>
{
return {
  myName:state.name
}

const mapDispatchToProps = (dispatch)=>{

  return {
    changeName:(name)=>{
      dispatch({
        type:'CHANGE_NAME',
        payload:name
      })
    }
  }
}
}
export default  connect(mapTostateProps)(App);
